import grpc
import os
import keras_image_helper
#import tensorflow as tf
from tensorflow_serving.apis import predict_pb2
from keras_image_helper import create_preprocessor
from tensorflow_serving.apis import prediction_service_pb2_grpc
from flask import Flask, request, jsonify
from proto import np_to_protobuf

classes = ['cup','fork', 'glass', 'knife', 'plate', 'spoon']

host = os.getenv('TF_SERVING_HOST','localhost:8500')
channel = grpc.insecure_channel(host)
stub = prediction_service_pb2_grpc.PredictionServiceStub(channel)
preprocessor = create_preprocessor('xception', target_size= (299, 299))


def perpare_request(data):
    pb_request = predict_pb2.PredictRequest()
    pb_request.model_spec.name = 'utensils'
    pb_request.model_spec.signature_name = 'serving_default'
    pb_request.inputs['input_2'].CopyFrom(np_to_protobuf(data))
    return pb_request

def prepare_response(pb_response):
    pred = pb_response.outputs['dense_1'].float_val
    out = dict(zip(classes, pred))
    return out

def prediction(url):

    x = preprocessor.from_url(url)
    request = perpare_request(x)
    pb_response = stub.Predict(request, timeout=20.0)
    result = prepare_response(pb_response)
    return result

app = Flask('kitchenware')
@app.route('/predict', methods = ['POST'])
def predict_endpoint():
    
    data = request.get_json()
    url = data['url']
    result = prediction(url)
    return jsonify(result)

